    @Override
    protected boolean validateArgs() {
        if (!ReaderHelper.isDirectoryExists(outputDirectory)) {
            log.info(String.format("Output directory [%s] does not exist", outputDirectory));
            return false;
        }

        if (StringUtils.isBlank(configFilePath)) {
            log.info("Config file path is required");
            return false;
        }

        String configFile = ReaderHelper.readFileAsString(configFilePath);
        if (StringUtils.isBlank(configFile)) {
            log.info(String.format("Config file [%s] is empty", configFilePath));
            return false;
        }

        CsrConfigDto configFromFile = JsonUtils.readJson(configFile, CsrConfigDto.class);
        if (Objects.isNull(configFromFile)) {
            log.info(String.format("Config file [%s] is invalid", configFilePath));
            return false;
        }

        if (configFromFile.getKeySize() > 0) {
            csrConfigDto.setKeySize(configFromFile.getKeySize());
        }
        if (StringUtils.isNotBlank(configFromFile.getTemplateOid())) {
            csrConfigDto.setTemplateOid(configFromFile.getTemplateOid());
        }
        if (configFromFile.getMajorVersion() > 0) {
            csrConfigDto.setMajorVersion(configFromFile.getMajorVersion());
        }
        if (configFromFile.getMinorVersion() >= 0) {
            csrConfigDto.setMinorVersion(configFromFile.getMinorVersion());
        }

        return true;
    }

    @Override
    protected boolean process() {
        CsrKeysProcessor csrKeysProcessor = new CsrKeysProcessor();
        String[] csrArgs = {outputDirectory, csrConfigDto.getEnName(), csrConfigDto.getSerialNumber(), 
                           csrConfigDto.getKeyPassword(), configFilePath};
        boolean isValid = csrKeysProcessor.process(csrArgs, propertiesManager);
        if (!isValid) {
            log.error("Failed to generate CSR and keys");
            return false;
        }

        if(!loadPrivateKey()){
            log.info("Failed to load private key");
            return false;
        }
        if(!loadCsrConfigs()){
            log.info("Failed to load CSR configs");
            return false;
        }
        if(!complianceCsr()){
            log.info("Failed to compliance csr");
            return false;
        }
        if(!enrichTestQueue()){
            log.info("Failed to create test xmls");
            return false;
        }
        if(!complianceInvoices()){
            log.info("Failed to compliance invoices");
            return false;
        }
        if(!getProdCertificate()){
            log.info("Failed to get prod certificate");
            return false;
        }
        return true;
    }

    @Override
    protected boolean output() {
        boolean valid=true;
        for(String key:signedXmlMap.keySet()){
            valid= WriterHelper.writeFile(outputDirectory+"/"+key,signedXmlMap.get(key)) && valid;
        }
        String productCertificate = outputDirectory + "/production_csid.cert";
        String productionResponse = outputDirectory + "/production_response.json";
        valid= WriterHelper.writeFile(productionResponse,SecurityUtils.encrypt(JsonUtils.toJson(prodCertificateResponse))) && valid;
        valid= WriterHelper.writeFile(productCertificate,
                SecurityUtils.encrypt(new String(Base64.getDecoder().decode(prodCertificateResponse.getBinarySecurityToken()),StandardCharsets.UTF_8)))
                && valid;
        return valid;
    }

    private boolean loadCsrConfigs() {
        try {
            String timestamp = findLatestTimestamp();
            if (timestamp == null) {
                log.error("No CSR files found in output directory");
                return false;
            }

            String commonName = extractCommonNameFromDN(csrConfigDto.getSubjectDn());
            String baseFileName = String.format("%s_%s", commonName, timestamp);
            String csrFile = outputDirectory + "/" + baseFileName + ".csr";

            csrEncoded = SecurityUtils.decrypt(ReaderHelper.readFileAsString(csrFile));

            String[] serialNumberParts = StringUtils.split(csrConfigDto.getSerialNumber(), "|");
            if (serialNumberParts.length >= 3) {
                deviceId = serialNumberParts[2];
                taxPayerNumber = serialNumberParts[0];
            } else {
                deviceId = "1";
                taxPayerNumber = csrConfigDto.getSerialNumber();
            }
        }catch (Exception e){
            log.error("Failed to load CSR configs",e);
            return false;
        }
        return true;
    }

    private boolean loadPrivateKey() {
        try {
            String timestamp = findLatestTimestamp();
            if (timestamp == null) {
                log.error("No private key files found in output directory");
                return false;
            }

            String commonName = extractCommonNameFromDN(csrConfigDto.getSubjectDn());
            String baseFileName = String.format("%s_%s", commonName, timestamp);
            String keyFile = outputDirectory + "/" + baseFileName + ".key";

            String privateKeyBase64 = SecurityUtils.decrypt(ReaderHelper.readFileAsString(keyFile));
            privateKey = PrivateKeyUtil.loadPrivateKey(privateKeyBase64, csrConfigDto.getKeyPassword());

        } catch (Exception e) {
            log.error("Failed to load private key", e);
            return false;
        }
        return true;
    }

    private boolean getProdCertificate() {
        prodCertificateResponse=client.getProdCertificate(complianceCsrResponse,complianceCsrResponse.getRequestID());
        return Objects.nonNull(prodCertificateResponse) && StringUtils.equalsIgnoreCase(prodCertificateResponse.getDispositionMessage(),"ISSUED");
    }

    private boolean complianceInvoices() {
        boolean valid=true;
        int counter=0;
        while (!testQueue.isEmpty()) {
            String xml=testQueue.poll();
            EInvoiceSigningResults signingResults=signingHelper.signEInvoice(xml,privateKey,complianceCertificateStr);

            String jsonBody= requesterGeneratorHelper.generateEInvoiceRequest(signingResults.getInvoiceHash(),signingResults.getInvoiceUUID(),signingResults.getSignedXml());
            ComplianceInvoiceResponse complianceInvoiceResponse=client.complianceInvoice(complianceCsrResponse,jsonBody);
            if(Objects.isNull(complianceInvoiceResponse) || BooleanUtils.isNotTrue(complianceInvoiceResponse.isValid())){
               log.info(String.format("Failed to compliance invoice [%s] and error [%s]",
                       Base64.getEncoder().encodeToString(xml.getBytes(StandardCharsets.UTF_8)),
                       JsonUtils.toJson(complianceInvoiceResponse)));
                valid=false;
            }else{
                String id="einvoice_test_"+taxPayerNumber+"_"+deviceId+"_"+(counter++)+".xml";
                signedXmlMap.put(id,signingResults.getSignedXml());
            }
        }
        return valid;
    }

    private boolean complianceCsr() {
        complianceCsrResponse=client.complianceCsr(otp,csrEncoded);
        complianceCertificateStr=new String(Base64.getDecoder().decode(complianceCsrResponse.getBinarySecurityToken()),StandardCharsets.UTF_8);
        return Objects.nonNull(complianceCsrResponse) && StringUtils.equalsIgnoreCase(complianceCsrResponse.getDispositionMessage(),"ISSUED");
    }

    private boolean enrichTestQueue() {
        boolean valid=false;
        try {
            int counter=0;
            testQueue.add(enrichFile(Objects.requireNonNull(ReaderHelper.readFileFromResource("samples/b2b_invoice.xml")),counter++));
            testQueue.add(enrichFile(Objects.requireNonNull(ReaderHelper.readFileFromResource("samples/b2b_credit.xml")),counter++));
            valid=true;
        }catch (Exception e){
            log.error("Failed to enrich test queue",e);
            return false;
        }
        return valid;
    }

    private String enrichFile(String file,int counter) {
        String id=taxPayerNumber+"_"+deviceId+"_"+counter;
        String orgId=taxPayerNumber+"_"+deviceId+"_"+(counter-1);
        LocalDateTime now = LocalDateTime.now();
        String formattedDate = now.format(formatter);
        String enrichedFile = file.replace("${ID}", id);
        enrichedFile = enrichedFile.replace("${UUID}", UUID.nameUUIDFromBytes(id.getBytes(StandardCharsets.UTF_8)).toString());
        enrichedFile = enrichedFile.replace("${ISSUE_DATE}", formattedDate);
        enrichedFile = enrichedFile.replace("${ORG_ID}", orgId);
        enrichedFile = enrichedFile.replace("${ORG_UUID}", UUID.nameUUIDFromBytes(orgId.getBytes(StandardCharsets.UTF_8)).toString());
        enrichedFile = enrichedFile.replace("${VAT_NUMBER}", taxPayerNumber);
        enrichedFile = enrichedFile.replace("${TAXPAYER_NAME}", csrConfigDto.getEnName());
        enrichedFile= enrichedFile.replace("${DEVICE_ID}", deviceId);
        return enrichedFile;
    }

    private String findLatestTimestamp() {
        try {
            java.io.File dir = new java.io.File(outputDirectory);
            java.io.File[] files = dir.listFiles((d, name) -> name.endsWith(".csr") || name.endsWith(".key"));
            if (files == null || files.length == 0) {
                return null;
            }

            String latestTimestamp = null;
            for (java.io.File file : files) {
                String fileName = file.getName();
                String[] parts = fileName.split("_");
                if (parts.length >= 2) {
                    String timestamp = parts[parts.length - 1].replace(".csr", "").replace(".key", "");
                    if (latestTimestamp == null || timestamp.compareTo(latestTimestamp) > 0) {
                        latestTimestamp = timestamp;
                    }
                }
            }
            return latestTimestamp;
        } catch (Exception e) {
            log.error("Failed to find latest timestamp", e);
            return null;
        }
    }

    private String extractCommonNameFromDN(String subjectDn) {
        try {
            String[] parts = subjectDn.split(",");
            for (String part : parts) {
                String trimmed = part.trim();
                if (trimmed.toUpperCase().startsWith("CN=")) {
                    return trimmed.substring(3).trim().replaceAll("[^a-zA-Z0-9_-]", "_");
                }
            }
            return "CSR";
        } catch (Exception e) {
            return "CSR";
        }
    }
}

package gov.jordan.istd.processor.impl;

import gov.jordan.istd.dto.EInvoiceSigningResults;
import gov.jordan.istd.helper.SigningHelper;
import gov.jordan.istd.io.ReaderHelper;
import gov.jordan.istd.processor.ActionProcessor;
import gov.jordan.istd.security.SecurityUtils;
import gov.jordan.istd.utils.ECDSAUtil;
import org.apache.commons.lang3.StringUtils;

import java.security.PrivateKey;
import java.util.Objects;

public class QrGeneratorProcessor extends ActionProcessor {
    private final SigningHelper signingHelper = new SigningHelper();
    private String xmlPath = "";
    private String privateKeyPath = "";
    private String certificatePath = "";
    private PrivateKey privateKey;
    private String xmlFile;
    private String certificateStr;
    private EInvoiceSigningResults signingResults;

    @Override
    protected boolean loadArgs(String[] args) {
        if (args.length != 3) {
            System.out.println("Usage: java -jar fotara-sdk.jar invoice-sign <xml-path> <private-key-path> <certificate-path>");
            return false;
        }
        xmlPath = args[0];
        privateKeyPath = args[1];
        certificatePath = args[2];
        return true;
    }

    @Override
    protected boolean validateArgs() {
        if (!readXmlFile()) return false;
        if (!readPrivateKey()) return false;
        return readCertificate();
    }

    @Override
    protected boolean process() {
        signingResults = signingHelper.signEInvoice(xmlFile, privateKey, certificateStr);
        return Objects.nonNull(signingResults) && StringUtils.isNotBlank(signingResults.getQrCode());
    }

    @Override
    protected boolean output() {
        log.info(String.format("invoice Hash [%s]\n invoice QR Code: [%s]\n", signingResults.getInvoiceHash(), signingResults.getQrCode()));
        return true;
    }

    private boolean readCertificate() {
        certificateStr = ReaderHelper.readFileAsString(certificatePath);
        certificateStr=SecurityUtils.decrypt(certificateStr);
        return StringUtils.isNotBlank(certificateStr);
    }

    private boolean readPrivateKey() {
        String privateKeyFile = ReaderHelper.readFileAsString(privateKeyPath);
        if (StringUtils.isBlank(privateKeyFile)) {
           log.info(String.format("Private key file [%s] is empty\n", privateKeyPath));
            return false;
        }
        try {
            privateKeyFile= SecurityUtils.decrypt(privateKeyFile);
            String key=privateKeyFile.replace("-----BEGIN EC PRIVATE KEY-----", "").replaceAll(System.lineSeparator(), "").replace("-----END EC PRIVATE KEY-----", "");
            privateKey = ECDSAUtil.getPrivateKey(key);
        } catch (Exception e) {
            log.info(String.format("Failed to read private key [%s]", privateKeyPath));
            return false;
        }
        return true;
    }


    private boolean readXmlFile() {
        xmlFile = ReaderHelper.readFileAsString(xmlPath);
        if (StringUtils.isBlank(xmlFile)) {
            log.info(String.format("XML file [%s] is empty", xmlPath));
            return false;
        }
        return true;
    }
}

package gov.jordan.istd.processor.impl;

import gov.jordan.istd.dto.EInvoiceSigningResults;
import gov.jordan.istd.helper.SigningHelper;
import gov.jordan.istd.io.ReaderHelper;
import gov.jordan.istd.processor.ActionProcessor;
import gov.jordan.istd.security.SecurityUtils;
import gov.jordan.istd.utils.PrivateKeyUtil;
import org.apache.commons.lang3.StringUtils;

import java.security.PrivateKey;
import java.util.Objects;

public class QrGeneratorProcessor extends ActionProcessor {
    private final SigningHelper signingHelper = new SigningHelper();
    private String xmlPath = "";
    private String privateKeyPath = "";
    private String certificatePath = "";
    private String keyPassword = "";
    private PrivateKey privateKey;
    private String xmlFile;
    private String certificateStr;
    private EInvoiceSigningResults signingResults;

    @Override
    protected boolean loadArgs(String[] args) {
        if (args.length < 3 || args.length > 4) {
            System.out.println("Usage: java -jar fotara-sdk.jar qr-generator <xml-path> <private-key-path> <certificate-path> [key-password]");
            return false;
        }
        xmlPath = args[0];
        privateKeyPath = args[1];
        certificatePath = args[2];
        if (args.length == 4) {
            keyPassword = args[3];
        }
        return true;
    }

    @Override
    protected boolean validateArgs() {
        if (!readXmlFile()) return false;
        if (!readPrivateKey()) return false;
        return readCertificate();
    }

    @Override
    protected boolean process() {
        signingResults = signingHelper.signEInvoice(xmlFile, privateKey, certificateStr);
        return Objects.nonNull(signingResults) && StringUtils.isNotBlank(signingResults.getQrCode());
    }

    @Override
    protected boolean output() {
        log.info(String.format("invoice Hash [%s]\n invoice QR Code: [%s]\n", signingResults.getInvoiceHash(), signingResults.getQrCode()));
        return true;
    }

    private boolean readCertificate() {
        certificateStr = ReaderHelper.readFileAsString(certificatePath);
        certificateStr=SecurityUtils.decrypt(certificateStr);
        return StringUtils.isNotBlank(certificateStr);
    }

    private boolean readPrivateKey() {
        String privateKeyFile = ReaderHelper.readFileAsString(privateKeyPath);
        if (StringUtils.isBlank(privateKeyFile)) {
           log.info(String.format("Private key file [%s] is empty\n", privateKeyPath));
            return false;
        }
        try {
            privateKeyFile = SecurityUtils.decrypt(privateKeyFile);
            privateKey = PrivateKeyUtil.loadPrivateKey(privateKeyFile, keyPassword);
        } catch (Exception e) {
            log.error(String.format("Failed to read private key [%s]", privateKeyPath), e);
            return false;
        }
        return true;
    }

    private boolean readXmlFile() {
        xmlFile = ReaderHelper.readFileAsString(xmlPath);
        if (StringUtils.isBlank(xmlFile)) {
            log.info(String.format("XML file [%s] is empty", xmlPath));
            return false;
        }
        return true;
    }
}

package gov.jordan.istd.utils;

import gov.jordan.istd.dto.CsrConfigDto;
import gov.jordan.istd.dto.CsrResponseDto;
import org.bouncycastle.asn1.*;
import org.bouncycastle.asn1.pkcs.PKCSObjectIdentifiers;
import org.bouncycastle.asn1.x500.X500Name;
import org.bouncycastle.asn1.x509.*;
import org.bouncycastle.operator.ContentSigner;
import org.bouncycastle.operator.jcajce.JcaContentSignerBuilder;
import org.bouncycastle.pkcs.PKCS10CertificationRequest;
import org.bouncycastle.pkcs.PKCS10CertificationRequestBuilder;
import org.bouncycastle.pkcs.jcajce.JcaPKCS10CertificationRequestBuilder;

import javax.crypto.Cipher;
import javax.crypto.EncryptedPrivateKeyInfo;
import javax.crypto.SecretKey;
import javax.crypto.SecretKeyFactory;
import javax.crypto.spec.PBEKeySpec;
import javax.crypto.spec.PBEParameterSpec;
import java.security.*;
import java.security.spec.PKCS8EncodedKeySpec;

/**
 * Java equivalent of C# CmsRequestHelper class
 * Provides CSR generation with RSA keys, Subject Key Identifier, and Certificate Template extensions
 */
public class CmsRequestHelper {

    /**
     * Create CSR with RSA keys and extensions - Java equivalent of C# CreateCsr method
     *
     * @param config Configuration object containing all CSR parameters
     * @return CsrResponseDto containing DER-encoded CSR and encrypted private key
     * @throws Exception if CSR generation fails
     */
    public static CsrResponseDto createCsr(CsrConfigDto config) throws Exception {
        if (config.getPassword() == null || config.getPassword().trim().isEmpty()) {
            throw new IllegalArgumentException("Password must not be null or empty.");
        }

        // Generate RSA key pair (equivalent to C# RSA.Create(keySize))
        KeyPairGenerator keyGen = KeyPairGenerator.getInstance("RSA");
        keyGen.initialize(config.getKeySize());
        KeyPair keyPair = keyGen.generateKeyPair();

        // Create subject from DN string (equivalent to C# CertificateRequest)
        X500Name subject = new X500Name(config.getSubjectDn());

        // Build CSR (equivalent to C# CertificateRequest)
        PKCS10CertificationRequestBuilder builder =
            new JcaPKCS10CertificationRequestBuilder(subject, keyPair.getPublic());

        // Add extensions
        ExtensionsGenerator extGen = new ExtensionsGenerator();

        // Subject Key Identifier (SKI) - equivalent to C# SKI creation
        addSubjectKeyIdentifier(extGen, keyPair.getPublic());

        // Certificate Template Extension (if provided) - Microsoft OID
        if (config.getTemplateOid() != null && !config.getTemplateOid().isEmpty()) {
            addCertificateTemplateExtension(extGen, config.getTemplateOid(),
                                          config.getMajorVersion(), config.getMinorVersion());
        }

        // Add extensions to CSR
        Extensions extensions = extGen.generate();
        builder.addAttribute(PKCSObjectIdentifiers.pkcs_9_at_extensionRequest, extensions);

        // Sign the CSR (equivalent to C# CreateSigningRequest with SHA256withRSA and PKCS1 padding)
        ContentSigner signer = new JcaContentSignerBuilder("SHA256withRSA").build(keyPair.getPrivate());
        PKCS10CertificationRequest csr = builder.build(signer);

        // Get CSR DER bytes (equivalent to C# CreateSigningRequest())
        byte[] pkcs10Der = csr.getEncoded();

        // Encrypt private key (equivalent to C# ExportEncryptedPkcs8PrivateKey)
        byte[] privateKeyBytes = exportEncryptedPkcs8PrivateKey(keyPair.getPrivate(), config.getPassword());

        return new CsrResponseDto(pkcs10Der, privateKeyBytes);
    }

    /**
     * Add Subject Key Identifier extension (equivalent to C# SKI creation)
     * Matches the exact C# implementation: SHA1 hash of public key + AsnWriter.WriteOctetString
     */
    private static void addSubjectKeyIdentifier(ExtensionsGenerator extGen, PublicKey publicKey) throws Exception {
        // Get public key bytes (equivalent to C# ExportSubjectPublicKeyInfo)
        byte[] publicKeyBytes = publicKey.getEncoded();

        // Calculate SHA-1 hash (equivalent to C# SHA1.HashData)
        MessageDigest sha1 = MessageDigest.getInstance("SHA-1");
        byte[] skiBytes = sha1.digest(publicKeyBytes);

        // Create SKI extension using DEROctetString (equivalent to C# AsnWriter.WriteOctetString)
        // OID "2.5.29.14" = Subject Key Identifier
        extGen.addExtension(new ASN1ObjectIdentifier("2.5.29.14"), false, new DEROctetString(skiBytes));
    }

    /**
     * Add Certificate Template extension (equivalent to C# BuildCertificateTemplateExtension)
     */
    private static void addCertificateTemplateExtension(ExtensionsGenerator extGen, String oid,
                                                       int majorVersion, int minorVersion) throws Exception {
        // Build template extension (equivalent to C# BuildCertificateTemplateExtension)
        byte[] templateExtension = buildCertificateTemplateExtension(oid, majorVersion, minorVersion);

        // Microsoft Certificate Template OID: 1.3.6.1.4.1.311.21.7
        extGen.addExtension(new ASN1ObjectIdentifier("1.3.6.1.4.1.311.21.7"),
                           false, new DEROctetString(templateExtension));
    }

    /**
     * Build Certificate Template extension (equivalent to C# BuildCertificateTemplateExtension method)
     * Exactly matches C#: AsnWriter.PushSequence() -> WriteObjectIdentifier -> WriteInteger -> WriteInteger -> PopSequence
     */
    private static byte[] buildCertificateTemplateExtension(String oid, int majorVersion, int minorVersion) throws Exception {
        ASN1EncodableVector templateVector = new ASN1EncodableVector();

        // Add template OID (equivalent to C# writer.WriteObjectIdentifier)
        templateVector.add(new ASN1ObjectIdentifier(oid));

        // Add major version (equivalent to C# writer.WriteInteger)
        templateVector.add(new ASN1Integer(majorVersion));

        // Add minor version (equivalent to C# writer.WriteInteger)
        templateVector.add(new ASN1Integer(minorVersion));

        // Create sequence (equivalent to C# PushSequence/PopSequence)
        DERSequence templateSequence = new DERSequence(templateVector);

        return templateSequence.getEncoded();
    }

    /**
     * Encrypt private key using PKCS#8 with AES256-CBC (equivalent to C# ExportEncryptedPkcs8PrivateKey)
     * Matches C#: PbeParameters(PbeEncryptionAlgorithm.Aes256Cbc, HashAlgorithmName.SHA256, 100000)
     */
    private static byte[] exportEncryptedPkcs8PrivateKey(PrivateKey privateKey, String password) throws Exception {
        try {
            // Use the Java equivalent of C# PbeParameters(PbeEncryptionAlgorithm.Aes256Cbc, HashAlgorithmName.SHA256, 100000)
            // Java equivalent: PBES2 with HMAC-SHA256 and AES-256-CBC
            SecretKeyFactory secretKeyFactory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA256");

            // Generate salt (16 bytes for AES)
            byte[] salt = new byte[16];
            SecureRandom.getInstanceStrong().nextBytes(salt);

            // Create PBE key spec with 100,000 iterations (matching C#)
            PBEKeySpec pbeKeySpec = new PBEKeySpec(password.toCharArray(), salt, 100000, 256); // 256-bit key for AES-256
            SecretKey secretKey = secretKeyFactory.generateSecret(pbeKeySpec);

            // Use AES-256-CBC for encryption (matching C# Aes256Cbc)
            Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");

            // Generate IV for CBC mode
            byte[] iv = new byte[16];
            SecureRandom.getInstanceStrong().nextBytes(iv);
            javax.crypto.spec.IvParameterSpec ivSpec = new javax.crypto.spec.IvParameterSpec(iv);

            // Initialize cipher with the derived key
            javax.crypto.spec.SecretKeySpec aesKey = new javax.crypto.spec.SecretKeySpec(secretKey.getEncoded(), "AES");
            cipher.init(Cipher.ENCRYPT_MODE, aesKey, ivSpec);

            // Encrypt the private key
            byte[] privateKeyBytes = privateKey.getEncoded();
            byte[] encryptedBytes = cipher.doFinal(privateKeyBytes);

            // Create PKCS#8 EncryptedPrivateKeyInfo manually using BouncyCastle
            // This matches the C# ExportEncryptedPkcs8PrivateKey format
            org.bouncycastle.asn1.ASN1EncodableVector algorithmVector = new org.bouncycastle.asn1.ASN1EncodableVector();

            // PBES2 algorithm identifier
            algorithmVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.1.5.13")); // PBES2

            // PBES2 parameters
            org.bouncycastle.asn1.ASN1EncodableVector pbes2Params = new org.bouncycastle.asn1.ASN1EncodableVector();

            // Key derivation function (PBKDF2 with HMAC-SHA256)
            org.bouncycastle.asn1.ASN1EncodableVector kdfVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            kdfVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.1.5.12")); // PBKDF2

            org.bouncycastle.asn1.ASN1EncodableVector pbkdf2Params = new org.bouncycastle.asn1.ASN1EncodableVector();
            pbkdf2Params.add(new org.bouncycastle.asn1.DEROctetString(salt));
            pbkdf2Params.add(new org.bouncycastle.asn1.ASN1Integer(100000)); // iteration count
            pbkdf2Params.add(new org.bouncycastle.asn1.ASN1Integer(32)); // key length (256 bits = 32 bytes)

            // HMAC-SHA256 identifier
            org.bouncycastle.asn1.ASN1EncodableVector hmacVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            hmacVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.2.9")); // HMAC-SHA256
            pbkdf2Params.add(new org.bouncycastle.asn1.DERSequence(hmacVector));

            kdfVector.add(new org.bouncycastle.asn1.DERSequence(pbkdf2Params));
            pbes2Params.add(new org.bouncycastle.asn1.DERSequence(kdfVector));

            // Encryption scheme (AES-256-CBC)
            org.bouncycastle.asn1.ASN1EncodableVector encSchemeVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            encSchemeVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("2.16.840.1.101.3.4.1.42")); // AES-256-CBC
            encSchemeVector.add(new org.bouncycastle.asn1.DEROctetString(iv));
            pbes2Params.add(new org.bouncycastle.asn1.DERSequence(encSchemeVector));

            algorithmVector.add(new org.bouncycastle.asn1.DERSequence(pbes2Params));

            // Create the complete EncryptedPrivateKeyInfo structure
            org.bouncycastle.asn1.ASN1EncodableVector encPrivKeyVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            encPrivKeyVector.add(new org.bouncycastle.asn1.DERSequence(algorithmVector));
            encPrivKeyVector.add(new org.bouncycastle.asn1.DEROctetString(encryptedBytes));

            org.bouncycastle.asn1.DERSequence encPrivKeyInfo = new org.bouncycastle.asn1.DERSequence(encPrivKeyVector);

            return encPrivKeyInfo.getEncoded();

        } catch (Exception e) {
            throw new Exception("Failed to encrypt private key: " + e.getMessage(), e);
        }
    }
}

package gov.jordan.istd.utils;

import gov.jordan.istd.dto.CsrConfigDto;
import gov.jordan.istd.dto.CsrResponseDto;
import org.bouncycastle.asn1.*;
import org.bouncycastle.asn1.pkcs.PKCSObjectIdentifiers;
import org.bouncycastle.asn1.x500.X500Name;
import org.bouncycastle.asn1.x509.*;
import org.bouncycastle.operator.ContentSigner;
import org.bouncycastle.operator.jcajce.JcaContentSignerBuilder;
import org.bouncycastle.pkcs.PKCS10CertificationRequest;
import org.bouncycastle.pkcs.PKCS10CertificationRequestBuilder;
import org.bouncycastle.pkcs.jcajce.JcaPKCS10CertificationRequestBuilder;

import javax.crypto.Cipher;
import javax.crypto.EncryptedPrivateKeyInfo;
import javax.crypto.SecretKey;
import javax.crypto.SecretKeyFactory;
import javax.crypto.spec.PBEKeySpec;
import javax.crypto.spec.PBEParameterSpec;
import java.security.*;
import java.security.spec.PKCS8EncodedKeySpec;

public class CmsRequestHelper {

    public static CsrResponseDto createCsr(CsrConfigDto config) throws Exception {
        if (config.getPassword() == null || config.getPassword().trim().isEmpty()) {
            throw new IllegalArgumentException("Password must not be null or empty.");
        }

        KeyPairGenerator keyGen = KeyPairGenerator.getInstance("RSA");
        keyGen.initialize(config.getKeySize());
        KeyPair keyPair = keyGen.generateKeyPair();

        X500Name subject = new X500Name(config.getSubjectDn());

        PKCS10CertificationRequestBuilder builder =
            new JcaPKCS10CertificationRequestBuilder(subject, keyPair.getPublic());

        ExtensionsGenerator extGen = new ExtensionsGenerator();

        addSubjectKeyIdentifier(extGen, keyPair.getPublic());

        if (config.getTemplateOid() != null && !config.getTemplateOid().isEmpty()) {
            addCertificateTemplateExtension(extGen, config.getTemplateOid(),
                                          config.getMajorVersion(), config.getMinorVersion());
        }

        Extensions extensions = extGen.generate();
        builder.addAttribute(PKCSObjectIdentifiers.pkcs_9_at_extensionRequest, extensions);

        ContentSigner signer = new JcaContentSignerBuilder("SHA256withRSA").build(keyPair.getPrivate());
        PKCS10CertificationRequest csr = builder.build(signer);

        byte[] pkcs10Der = csr.getEncoded();

        byte[] privateKeyBytes = exportEncryptedPkcs8PrivateKey(keyPair.getPrivate(), config.getPassword());

        return new CsrResponseDto(pkcs10Der, privateKeyBytes);
    }

    private static void addSubjectKeyIdentifier(ExtensionsGenerator extGen, PublicKey publicKey) throws Exception {
        byte[] publicKeyBytes = publicKey.getEncoded();

        MessageDigest sha1 = MessageDigest.getInstance("SHA-1");
        byte[] skiBytes = sha1.digest(publicKeyBytes);

        extGen.addExtension(new ASN1ObjectIdentifier("2.5.29.14"), false, new DEROctetString(skiBytes));
    }

    private static void addCertificateTemplateExtension(ExtensionsGenerator extGen, String oid,
                                                       int majorVersion, int minorVersion) throws Exception {
        byte[] templateExtension = buildCertificateTemplateExtension(oid, majorVersion, minorVersion);

        extGen.addExtension(new ASN1ObjectIdentifier("1.3.6.1.4.1.311.21.7"),
                           false, new DEROctetString(templateExtension));
    }

    private static byte[] buildCertificateTemplateExtension(String oid, int majorVersion, int minorVersion) throws Exception {
        ASN1EncodableVector templateVector = new ASN1EncodableVector();

        templateVector.add(new ASN1ObjectIdentifier(oid));

        templateVector.add(new ASN1Integer(majorVersion));

        templateVector.add(new ASN1Integer(minorVersion));

        DERSequence templateSequence = new DERSequence(templateVector);

        return templateSequence.getEncoded();
    }

    private static byte[] exportEncryptedPkcs8PrivateKey(PrivateKey privateKey, String password) throws Exception {
        try {
            SecretKeyFactory secretKeyFactory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA256");

            byte[] salt = new byte[16];
            SecureRandom.getInstanceStrong().nextBytes(salt);

            PBEKeySpec pbeKeySpec = new PBEKeySpec(password.toCharArray(), salt, 100000, 256);
            SecretKey secretKey = secretKeyFactory.generateSecret(pbeKeySpec);

            Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");

            byte[] iv = new byte[16];
            SecureRandom.getInstanceStrong().nextBytes(iv);
            javax.crypto.spec.IvParameterSpec ivSpec = new javax.crypto.spec.IvParameterSpec(iv);

            javax.crypto.spec.SecretKeySpec aesKey = new javax.crypto.spec.SecretKeySpec(secretKey.getEncoded(), "AES");
            cipher.init(Cipher.ENCRYPT_MODE, aesKey, ivSpec);

            byte[] privateKeyBytes = privateKey.getEncoded();
            byte[] encryptedBytes = cipher.doFinal(privateKeyBytes);

            org.bouncycastle.asn1.ASN1EncodableVector algorithmVector = new org.bouncycastle.asn1.ASN1EncodableVector();

            algorithmVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.1.5.13"));

            org.bouncycastle.asn1.ASN1EncodableVector pbes2Params = new org.bouncycastle.asn1.ASN1EncodableVector();

            org.bouncycastle.asn1.ASN1EncodableVector kdfVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            kdfVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.1.5.12"));

            org.bouncycastle.asn1.ASN1EncodableVector pbkdf2Params = new org.bouncycastle.asn1.ASN1EncodableVector();
            pbkdf2Params.add(new org.bouncycastle.asn1.DEROctetString(salt));
            pbkdf2Params.add(new org.bouncycastle.asn1.ASN1Integer(100000));
            pbkdf2Params.add(new org.bouncycastle.asn1.ASN1Integer(32));

            org.bouncycastle.asn1.ASN1EncodableVector hmacVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            hmacVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("1.2.840.113549.2.9"));
            pbkdf2Params.add(new org.bouncycastle.asn1.DERSequence(hmacVector));

            kdfVector.add(new org.bouncycastle.asn1.DERSequence(pbkdf2Params));
            pbes2Params.add(new org.bouncycastle.asn1.DERSequence(kdfVector));

            org.bouncycastle.asn1.ASN1EncodableVector encSchemeVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            encSchemeVector.add(new org.bouncycastle.asn1.ASN1ObjectIdentifier("2.16.840.1.101.3.4.1.42"));
            encSchemeVector.add(new org.bouncycastle.asn1.DEROctetString(iv));
            pbes2Params.add(new org.bouncycastle.asn1.DERSequence(encSchemeVector));

            algorithmVector.add(new org.bouncycastle.asn1.DERSequence(pbes2Params));

            org.bouncycastle.asn1.ASN1EncodableVector encPrivKeyVector = new org.bouncycastle.asn1.ASN1EncodableVector();
            encPrivKeyVector.add(new org.bouncycastle.asn1.DERSequence(algorithmVector));
            encPrivKeyVector.add(new org.bouncycastle.asn1.DEROctetString(encryptedBytes));

            org.bouncycastle.asn1.DERSequence encPrivKeyInfo = new org.bouncycastle.asn1.DERSequence(encPrivKeyVector);

            return encPrivKeyInfo.getEncoded();

        } catch (Exception e) {
            throw new Exception("Failed to encrypt private key: " + e.getMessage(), e);
        }
    }
}

package gov.jordan.istd.utils;

import java.util.Base64;

/**
 * Java equivalent of C# OtherFunctions class
 * Provides utility methods for PEM conversion and CSR string cleaning
 */
public class OtherFunctions {

    /**
     * Convert private key bytes to PEM format (equivalent to C# ConvertPrivateKeyBytesToPem)
     */
    public static String convertPrivateKeyBytesToPem(byte[] privateKeyBytes) {
        StringBuilder builder = new StringBuilder();
        builder.append("-----BEGIN ENCRYPTED PRIVATE KEY-----\n");
        builder.append(insertLineBreaks(Base64.getEncoder().encodeToString(privateKeyBytes), 64));
        builder.append("\n-----END ENCRYPTED PRIVATE KEY-----\n");
        return builder.toString();
    }

    /**
     * Convert certificate bytes to PEM format (equivalent to C# ConvertCertificateBytesToPem)
     */
    public static String convertCertificateBytesToPem(byte[] certBytes) {
        StringBuilder builder = new StringBuilder();
        builder.append("-----BEGIN CERTIFICATE-----\n");
        builder.append(insertLineBreaks(Base64.getEncoder().encodeToString(certBytes), 64));
        builder.append("\n-----END CERTIFICATE-----\n");
        return builder.toString();
    }

    /**
     * Clean CSR string by removing PEM headers and whitespace (equivalent to C# CleanCsrString)
     */
    public static String cleanCsrString(String rawCsr) {
        if (rawCsr == null || rawCsr.trim().isEmpty()) {
            return "";
        }

        return rawCsr
            .replaceAll("(?i)-----BEGIN CERTIFICATE REQUEST-----", "")
            .replaceAll("(?i)-----END CERTIFICATE REQUEST-----", "")
            .replace("\r", "")
            .replace("\n", "")
            .replace("\t", "")
            .trim();
    }

    /**
     * Insert line breaks every specified number of characters (equivalent to C# Base64FormattingOptions.InsertLineBreaks)
     */
    private static String insertLineBreaks(String input, int lineLength) {
        StringBuilder result = new StringBuilder();
        for (int i = 0; i < input.length(); i += lineLength) {
            result.append(input, i, Math.min(i + lineLength, input.length()));
            if (i + lineLength < input.length()) {
                result.append("\n");
            }
        }
        return result.toString();
    }
}

package gov.jordan.istd.utils;

import org.bouncycastle.asn1.pkcs.PrivateKeyInfo;
import org.bouncycastle.openssl.PEMParser;
import org.bouncycastle.openssl.jcajce.JcaPEMKeyConverter;
import org.bouncycastle.openssl.jcajce.JceOpenSSLPKCS8DecryptorProviderBuilder;
import org.bouncycastle.operator.InputDecryptorProvider;
import org.bouncycastle.pkcs.PKCS8EncryptedPrivateKeyInfo;

import java.io.StringReader;
import java.nio.charset.StandardCharsets;
import java.security.KeyFactory;
import java.security.PrivateKey;
import java.security.spec.PKCS8EncodedKeySpec;
import java.util.Base64;

public class PrivateKeyUtil {

    public static PrivateKey loadPrivateKey(String privateKeyContent, String password) throws Exception {
        if (privateKeyContent.contains("-----BEGIN ENCRYPTED PRIVATE KEY-----")) {
            return loadEncryptedPKCS8PrivateKey(privateKeyContent, password);
        } else if (privateKeyContent.contains("-----BEGIN PRIVATE KEY-----")) {
            return loadUnencryptedPKCS8PrivateKey(privateKeyContent);
        } else if (privateKeyContent.contains("-----BEGIN EC PRIVATE KEY-----")) {
            return loadECPrivateKey(privateKeyContent);
        } else if (privateKeyContent.contains("-----BEGIN RSA PRIVATE KEY-----")) {
            return loadRSAPrivateKey(privateKeyContent);
        } else {
            return loadPrivateKeyFromBase64(privateKeyContent, password);
        }
    }

    private static PrivateKey loadEncryptedPKCS8PrivateKey(String privateKeyContent, String password) throws Exception {
        try (PEMParser pemParser = new PEMParser(new StringReader(privateKeyContent))) {
            Object object = pemParser.readObject();
            JcaPEMKeyConverter converter = new JcaPEMKeyConverter();

            if (object instanceof PKCS8EncryptedPrivateKeyInfo) {
                PKCS8EncryptedPrivateKeyInfo encryptedPrivateKeyInfo = (PKCS8EncryptedPrivateKeyInfo) object;
                InputDecryptorProvider decryptorProvider = new JceOpenSSLPKCS8DecryptorProviderBuilder()
                        .build(password.toCharArray());
                return converter.getPrivateKey(encryptedPrivateKeyInfo.decryptPrivateKeyInfo(decryptorProvider));
            } else {
                throw new Exception("Expected encrypted private key but found different format");
            }
        }
    }

    private static PrivateKey loadUnencryptedPKCS8PrivateKey(String privateKeyContent) throws Exception {
        String cleanKey = privateKeyContent
                .replace("-----BEGIN PRIVATE KEY-----", "")
                .replace("-----END PRIVATE KEY-----", "")
                .replaceAll("\\s", "");

        byte[] keyBytes = Base64.getDecoder().decode(cleanKey);
        PKCS8EncodedKeySpec keySpec = new PKCS8EncodedKeySpec(keyBytes);

        try {
            KeyFactory keyFactory = KeyFactory.getInstance("RSA");
            return keyFactory.generatePrivate(keySpec);
        } catch (Exception e) {
            KeyFactory keyFactory = KeyFactory.getInstance("EC");
            return keyFactory.generatePrivate(keySpec);
        }
    }

    private static PrivateKey loadECPrivateKey(String privateKeyContent) throws Exception {
        String cleanKey = privateKeyContent
                .replace("-----BEGIN EC PRIVATE KEY-----", "")
                .replace("-----END EC PRIVATE KEY-----", "")
                .replaceAll("\\s", "");

        return ECDSAUtil.getPrivateKey(cleanKey);
    }

    private static PrivateKey loadRSAPrivateKey(String privateKeyContent) throws Exception {
        try (PEMParser pemParser = new PEMParser(new StringReader(privateKeyContent))) {
            Object object = pemParser.readObject();
            JcaPEMKeyConverter converter = new JcaPEMKeyConverter();

            if (object instanceof org.bouncycastle.openssl.PEMKeyPair) {
                org.bouncycastle.openssl.PEMKeyPair keyPair = (org.bouncycastle.openssl.PEMKeyPair) object;
                return converter.getPrivateKey(keyPair.getPrivateKeyInfo());
            } else if (object instanceof org.bouncycastle.pkcs.PKCS8EncryptedPrivateKeyInfo) {
                throw new Exception("Encrypted RSA private key detected - use password parameter");
            } else {
                return converter.getPrivateKey((PrivateKeyInfo) object);
            }
        }
    }

    private static PrivateKey loadPrivateKeyFromBase64(String privateKeyBase64, String password) throws Exception {
        byte[] privateKeyBytes = Base64.getDecoder().decode(privateKeyBase64);

        // Try to parse as PEM first
        String pemContent = new String(privateKeyBytes, StandardCharsets.UTF_8);
        if (pemContent.contains("-----BEGIN")) {
            return loadPrivateKey(pemContent, password);
        }

        // Try parsing as raw encrypted PKCS#8 DER data (not PEM)
        try {
            PKCS8EncryptedPrivateKeyInfo encryptedPrivateKeyInfo = new PKCS8EncryptedPrivateKeyInfo(privateKeyBytes);
            if (password != null && !password.isEmpty()) {
                InputDecryptorProvider decryptorProvider = new JceOpenSSLPKCS8DecryptorProviderBuilder()
                        .build(password.toCharArray());
                JcaPEMKeyConverter converter = new JcaPEMKeyConverter();
                return converter.getPrivateKey(encryptedPrivateKeyInfo.decryptPrivateKeyInfo(decryptorProvider));
            } else {
                throw new Exception("Encrypted private key requires password");
            }
        } catch (Exception encryptedException) {
            // If encrypted PKCS#8 parsing fails, try PEM parsing (in case it's PEM without headers)
            try (PEMParser pemParser = new PEMParser(new StringReader(pemContent))) {
                Object object = pemParser.readObject();
                if (object != null) {
                    JcaPEMKeyConverter converter = new JcaPEMKeyConverter();

                    if (object instanceof PKCS8EncryptedPrivateKeyInfo) {
                        PKCS8EncryptedPrivateKeyInfo encryptedPrivateKeyInfo = (PKCS8EncryptedPrivateKeyInfo) object;
                        if (password != null && !password.isEmpty()) {
                            InputDecryptorProvider decryptorProvider = new JceOpenSSLPKCS8DecryptorProviderBuilder()
                                    .build(password.toCharArray());
                            return converter.getPrivateKey(encryptedPrivateKeyInfo.decryptPrivateKeyInfo(decryptorProvider));
                        } else {
                            throw new Exception("Encrypted private key requires password");
                        }
                    } else {
                        return converter.getPrivateKey((PrivateKeyInfo) object);
                    }
                }
            } catch (Exception pemException) {
                // If PEM parsing fails, try raw unencrypted PKCS#8
                try {
                    PKCS8EncodedKeySpec keySpec = new PKCS8EncodedKeySpec(privateKeyBytes);

                    try {
                        KeyFactory keyFactory = KeyFactory.getInstance("RSA");
                        return keyFactory.generatePrivate(keySpec);
                    } catch (Exception rsaException) {
                        try {
                            KeyFactory keyFactory = KeyFactory.getInstance("EC");
                            return keyFactory.generatePrivate(keySpec);
                        } catch (Exception ecException) {
                            throw new Exception("Unable to parse private key format. Encrypted PKCS#8 error: " + encryptedException.getMessage() + 
                                              ", PEM error: " + pemException.getMessage() + 
                                              ", RSA error: " + rsaException.getMessage() + 
                                              ", EC error: " + ecException.getMessage());
                        }
                    }
                } catch (Exception rawException) {
                    throw new Exception("Unable to parse private key. All parsing attempts failed. " +
                                      "Encrypted PKCS#8 error: " + encryptedException.getMessage() + 
                                      ", PEM error: " + pemException.getMessage() + 
                                      ", Raw PKCS#8 error: " + rawException.getMessage());
                }
            }
        }

        throw new Exception("Unable to determine private key format");
    }
}
